mapper.update:

	update shopping_goods



            goods_inventory = goods_inventory -#{cutStock,jdbcType=INTEGER},




        and id = #{goodsId,jdbcType=VARCHAR }


	update shopping_goods_inventory



            count = count - #{cutCount,jdbcType=INTEGER},





        AND goods_id = #{goodsId ,jdbcType=VARCHAR}



        AND propertys = #{propertys ,jdbcType=VARCHAR}

mapper.select:

SELECT t.count FROM shopping_goods_inventory t



    AND goods_id = #{goodsId ,jdbcType=VARCHAR}



    AND propertys = #{propertys ,jdbcType=VARCHAR}
